import React, { useState } from 'react';
import { Input } from 'antd';
import '../node_modules/bootstrap/dist/css/bootstrap.min.css'
import { Card, Col, Row } from 'antd';
import { Button } from 'antd';
import { Form } from 'antd';



const Signup = () => {

    const [name, setname] = useState();
    const [password, setpassword] = useState();
    const inputname = (event) => {
        const val = event.target.value;
        setname(val)
        console.log(val);
    }
    const passwordname = (event) => {
        const pass = event.target.value;
        setpassword(pass)
        console.log(pass)
    }

    const submit = () => {
        localStorage.setItem('name', name);
        localStorage.setItem('pass', password);

    }

    return (
        <>
            <div className="container">


                <Card title="Signup" style={{ marginTop: "5%" }}>

                    <Form>





                        <label >Email</label>

                        <Input placeholder="Email" value={name} onChange={inputname} />
                        <label style={{ marginTop: "5%" }}>Password</label>
                        <Input placeholder="Password" value={password} onChange={passwordname} />
                        <Button type="submit" style={{
                            float: "right",
                            marginTop: "1%"
                        }} onClick={submit}  >Submit</Button>
                    </Form>
                </Card>,
            </div>
        </>
    )
}
export default Signup
