        setpassword(pass)

    }


    const signup = [{email,password,fname,lname,cpass}];

    const submit = () => {
        localStorage.setItem("signup", JSON.stringify(signup));
        // localStorage.setItem('email', name);
        // localStorage.setItem('pass', password);
        // localStorage.setItem('cpass', cpass);
        // localStorage.setItem('fname', fname);
        // localStorage.setItem('lname', lname);
    }






    return (
        <>
            <div className="container">


                <Card title="Signup" style={{ marginTop: "5%" }}>

                    <Form>





                        <label style={{ marginTop: "2%" }}>First Name*</label>
                        <Input placeholder="First Name" value={fname} onChange={chnageEvent} required />
                        <label style={{ marginTop: "2%" }}>Last Name</label>
                        <Input placeholder="Last Name" value={lname} onChange={chnageEvent} />
                        <label style={{ marginTop: "2%" }}>Email</label>
                        <Input placeholder="Email" value={email} onChange={chnageEvent} />
                        <label style={{ marginTop: "2%" }}>Password</label>
                        <Input placeholder="Password" value={password} onChange={chnageEvent} />
                        <label style={{ marginTop: "2%" }}>Confirm Password</label>
                        <Input placeholder="Confirm Password" value={cpass} onChange={chnageEvent} />

                        <Button type="submit" style={{
                            float: "right",
                            marginTop: "1%"
                        }} onClick={submit}  >Submit</Button>
                    </Form>
                </Card>,
            </div>
        </>
    )
}
