const PaginationFooter = (props) => {

const[counter,setCounter]=useState(1)
useEffect(()=>{
const value= props.showPerPage *counter;
// console.log("start Value:" , value-props.showPerPage)
// console.log("End Value:" , value)
props.onPageChange(value-props.showPerPage,value);
},[counter])

const onButtonClick =(type)=>{
if(type==='prev'){
if(counter === 1){
    setCounter(1)
}else{
    setCounter(counter-1)
}
}else if(type==='next'){

}
}

    return (
        <div className="d-flex justify-content-between ">
            <button className="btn btn-primary" onClick={()=>onButtonClick('prev')}>Previous</button>
            <button className="btn btn-primary" onClick={()=>onButtonClick('next')} >Next</button>
        </div>
    )
}

