import React, { useState } from 'react';
import { Input } from 'antd';
import '../node_modules/bootstrap/dist/css/bootstrap.min.css'
import { Card, Col, Row } from 'antd';
import { Button } from 'antd';
import { Form } from 'antd';



const Signup = () => {

    const [sign, setsign] = useState({
        email:'',
        password:'',
        fname:'',
        lname:'',
        cpass:''
    });



const chnageEvent=(event)=>{
setsign(event.target.value)
}






    const submit = (event) => {
           event.preventDefault();
           alert("Hello submitted successfully!!!")
        // localStorage.setItem("signup", JSON.stringify());
        // localStorage.setItem('email', name);
        // localStorage.setItem('pass', password);
        // localStorage.setItem('cpass', cpass);
        // localStorage.setItem('fname', fname);
        // localStorage.setItem('lname', lname);
    }






    return (
        <>
            <div className="container">


                <Card title="Signup" style={{ marginTop: "5%" }}>

                    <Form>
                        <label style={{ marginTop: "2%" }}>First Name*</label> 

                        <Input placeholder="First Name" value={sign.fname} name={fname} onChange={chnageEvent} required />
                        <label style={{ marginTop: "2%" }}>Last Name</label>
                        <Input placeholder="Last Name" value={sign.lname} name={lname} onChange={chnageEvent} />
                        <label style={{ marginTop: "2%" }}>Email</label>
                        <Input placeholder="Email" value={sign.email} name={email} onChange={chnageEvent} />
                        <label style={{ marginTop: "2%" }}>Password</label>
                        <Input placeholder="Password" name={password} value={sign.password} onChange={chnageEvent} />
                        <label style={{ marginTop: "2%" }}>Confirm Password</label>
                        <Input placeholder="Confirm Password" name={cpass} value={sign.cpass} onChange={chnageEvent} />      
                        <Button type="submit" style={{
                            float: "right",
                            marginTop: "1%"
                        }} onClick={submit}  >Submit</Button>
                    </Form>
                </Card>,
            </div>
        </>
    )
}
export default Signup
