import React, { useState, useEffect } from 'react'

const PaginationFooter = (props) => {

const[counter,setCounter]=useState(1)
useEffect(()=>{
const value= props.showPerPage *counter;
console.log("start Value:" , value-props.showPerPage)
console.log("End Value:" , value)
props.onPageChange(value-props.showPerPage,value);
},[counter])

    return (
        <div className="d-flex justify-content-between ">
            <button className="btn btn-primary" onClick={()=>setCounter(counter -1)}>Previous</button>
            <button className="btn btn-primary" onClick={()=>setCounter(counter +1)} >Next</button>
        </div>
    )
}

export default PaginationFooter
