import React, { useState } from 'react';
import { Input } from 'antd';
import '../node_modules/bootstrap/dist/css/bootstrap.min.css'
import { Card, Col, Row } from 'antd';
import { Button } from 'antd';
import { Form } from 'antd';



const Signup = () => {

    const [name, setname] = useState();
    const [password, setpassword] = useState();
    const [fname, setfname] = useState();
    const [lname, setlname] = useState();
    const [cpass, setcpass]= useState();


const firstname=(event)=>{
setfname(event.target.value)
}

const lastname=(event)=>{
    setlname(event.target.value)
}
const cpassword=(event)=>{
    setcpass(event.target.value)
}







    const inputname = (event) => {

        const val = event.target.value;
        setname(val)

    }
    const passwordname = (event) => {
        const pass = event.target.value;
        setpassword(pass)

    }


    const signup = [name,password,fname,lname,cpass];

    const submit = () => {
        localStorage.setItem("signup", JSON.stringify(names));
        localStorage.setItem('email', name);
        localStorage.setItem('pass', password);
        localStorage.setItem('cpass', cpass);
        localStorage.setItem('fname', fname);
        localStorage.setItem('lname', lname);
    }






    return (
        <>
            <div className="container">


                <Card title="Signup" style={{ marginTop: "5%" }}>

                    <Form>





                        <label style={{ marginTop: "2%" }}>First Name</label>
                        <Input placeholder="First Name" value={fname} onChange={firstname} />
                        <label style={{ marginTop: "2%" }}>Last Name</label>
                        <Input placeholder="Last Name" value={lname} onChange={lastname} />
                        <label style={{ marginTop: "2%" }}>Email</label>
                        <Input placeholder="Email" value={name} onChange={inputname} />
                        <label style={{ marginTop: "2%" }}>Password</label>
                        <Input placeholder="Password" value={password} onChange={passwordname} />
                        <label style={{ marginTop: "2%" }}>Confirm Password</label>
                        <Input placeholder="Confirm Password" value={cpass} onChange={cpassword} />


                        <Button type="submit" style={{
                            float: "right",
                            marginTop: "1%"
                        }} onClick={submit}  >Submit</Button>
                    </Form>
                </Card>,
            </div>
        </>
    )
}
export default Signup
